          expandedHeight: 140.0,              flexibleSpace: FlexibleSpaceBar(                collapseMode: CollapseMode.parallax,                background: Image.asset(AppAssets.images.testBackGroundProfile,fit: BoxFit.fitWidth,),              ),            ),            SliverToBoxAdapter(              child: SizedBox(                  child: Container(                    padding: const EdgeInsets.all(13),                    color: AppColors.mainBlack,                    child: Column(                      children: [                        Row(                          children: [                            CircleAvatar(radius: 46,                              backgroundImage: AssetImage(AppAssets.images.testProfile),),                            const SizedBox(                              width: 18,                            ),                            Column(                              children: [                                FittedBox(                                  child: Text(                                    '${parameters?.name}  ${parameters?.surname}',                                    style: GoogleFonts.inter(textStyle: AppStyles.s24w700)                                        .copyWith(color: AppColors.mainWhite),                                  ),                                ),                                Text(                                 '@${parameters?.nickname}',                                  style: GoogleFonts.inter(textStyle: AppStyles.s16w400)                                      .copyWith(color: AppColors.mainWhite),                                ),                              ],                            )                          ],                        ),                        Padding(                          padding: const EdgeInsets.symmetric(vertical: 30),                          child: Row(                            children: [                              Expanded(                                child: Column(                                  children: [                                    Text('${parameters?.followers}',                                        style: GoogleFonts.inter(textStyle: AppStyles.s24w700)                                            .copyWith(color: AppColors.mainWhite)),                                    Text("${AppLocalizations.of(context)?.followers}",style: GoogleFonts.inter(textStyle: AppStyles.s16w400).copyWith(color: AppColors.mainWhite)),                                  ],                                ),                              ),                              Expanded(                                child: Column(                                  children: [                                    Text('${parameters?.followings}',style: GoogleFonts.inter(textStyle: AppStyles.s24w700).copyWith(color: AppColors.mainWhite),),                                    Text("${AppLocalizations.of(context)?.followings}",style: GoogleFonts.inter(textStyle: AppStyles.s16w400).copyWith(color: AppColors.mainWhite)),                                  ],                                ),                              ),                              Expanded(                                child: Column(                                  children: [                                    Text('${parameters?.clubs}',style: GoogleFonts.inter(textStyle: AppStyles.s24w700).copyWith(color: AppColors.mainWhite)                                    ),                                    Text("${AppLocalizations.of(context)?.clubs}",style: GoogleFonts.inter(textStyle: AppStyles.s16w400).copyWith(color: AppColors.mainWhite)),                                  ],                                ),                              )                            ],                          ),                        ),                        const SizedBox(height: 15,),                        Row(                          children: [                            Expanded(                              child: SizedBox(                                width: 121,                                child: ElevatedButton(onPressed: (){}, style: ElevatedButton.styleFrom(                                    shape: const RoundedRectangleBorder(                                      borderRadius: BorderRadius.all(Radius.circular(20)),                                    )                                ), child: Text("${AppLocalizations.of(context)?.follow}",style: GoogleFonts.poppins(textStyle: AppStyles.s14w500),),),                              ),                            ),                            const SizedBox(width: 20,),                            Expanded(                              child: SizedBox(                                width: 121,                                child: ElevatedButton(onPressed: (){},style: ElevatedButton.styleFrom(                                    backgroundColor: AppColors.mainWhite,                                    foregroundColor: AppColors.mainBlack,                                    shape: const RoundedRectangleBorder(                                      borderRadius: BorderRadius.all(Radius.circular(20)),                                    )                                ), child: Text("${AppLocalizations.of(context)?.message}",style: GoogleFonts.poppins(textStyle: AppStyles.s14w500),)),                              ),                            ),                          ],                        )                      ],                    ),                  )              ),            ),          ];        },        body: Column(          children: [            Padding(              padding: const EdgeInsets.symmetric(horizontal: 60),              child: TabBar(                controller: tabController,                tabs: [                  Tab(                    child: Text("${AppLocalizations.of(context)?.all}",style: style,),                  ),                  Tab(                    child: Text("${AppLocalizations.of(context)?.posts}",style: style,),                  ),                  Tab(                    child: FittedBox(child: Text("${AppLocalizations.of(context)?.videos}",style: style,maxLines: 1,)),                  )                ],              ),            ),            Expanded(              child: Padding(                padding: const EdgeInsets.symmetric(horizontal: 6),                child: TabBarView(                  controller: tabController,                  children: const [                    PostsWidget(),                    Text('3'),                    Text('3'),                  ],                ),              ),            ),          ],        ),      ),    );  }}class ProfileParameters {  final String backgroundImage;  final String avatarImage;  final String nametag;  final String fullName;  final String followers;  final String followings;  final String clubs;  ProfileParameters({    required this.backgroundImage,    required this.avatarImage,    required this.nametag,    required this.fullName,    required this.followers,    required this.followings,    required this.clubs,  });}